import { Modal } from "../../BuildingBlocks/Modal/Modal";
import { BookModalProps } from "./types";

import { createBookEntry, getLocalBooks } from "../repository";

import styles from "./BookModal.module.scss";

const {
  bookModalWrapper,
  bookInfoWrapper,
  bookTitle,
  bookDescription,
  imageContainer,
} = styles;

const handleAddToDB = async (params: {
  google_fk: string | undefined;
  categories: string[];
}) => {
  if (!params.google_fk || !params.categories.length) {
    return;
  }
//   return await getLocalBooks();
    return await createBookEntry(params);
};

export const BookModal = ({ handleShowModal, book }: BookModalProps) => {
  return (
    <Modal handleClose={() => handleShowModal(null)}>
      <div className={bookModalWrapper}>
        <div className={bookInfoWrapper}>
          <div className={bookTitle}>{book?.title}</div>
          <div>{book?.authors}</div>
          <div className={bookDescription}>{book?.description}</div>
          <button
            onClick={() =>
              handleAddToDB({ google_fk: book?.id, categories: ["Erotica"] })
            }
          >
            Add to DB
          </button>
        </div>
        <div className={imageContainer}>
          <img src={book?.imageLink} />
          <a href={book?.previewLink} target="_blank">
            View in Google Books
          </a>
        </div>
      </div>
    </Modal>
  );
};
