import { useState, useEffect } from "react";
import { Books } from "./components/Books/Books";
import { SearchHeader } from "./components/SearchHeader/SearchHeader";
import { BookProps } from "./components/types";

import styles from "./App.module.scss";
import { createBook } from "./repository";
const { container } = styles;

const callBackendAPI = async () => {
  const body = await createBook();

  if (response.status !== 200) {
    throw Error(body.message);
  }
  return body;
};

function App() {
  const [books, setBooks] = useState<BookProps[]>([]);
  const [response, setResponse] = useState(null);

  useEffect(() => {
    callBackendAPI().then((res) => setResponse(res));
  }, []);

  return (
    <div className={container}>
      <SearchHeader handleSetBooks={setBooks} books={books} />
      <Books booksList={books} />
    </div>
  );
}

export default App;
