import { useState, useEffect } from "react";
import { useFormik, FormikProps } from "formik";

import { MultiSelect } from "../../BuildingBlocks/MultiSelect/MultiSelect";
import { BookProps, CategoryProps } from "../types";
import { getAllCategories } from "../../components/repository";

type NewCategoryProps = Pick<CategoryProps, "name">;

const handleAddToDB = async ({
  categories,
}: {
  categories: CategoryProps[];
}) => {
  if (!params.googleId || !params.categories.length) {
    return;
  }

  const newBook = await createBookEntry({
    googleId: params.googleId,
    categories: params.categories,
  });

  return { newBook };
};

export const BookActionsForm = (book: BookProps) => {
  const [allCategories, setAllCategoryOptions] = useState<CategoryProps[]>([]);

  useEffect(() => {
    const getCategories = async () => {
      const categoriesResponse = await getAllCategories();
      setAllCategoryOptions(categoriesResponse);
    };

    getCategories();
  }, []);

  const initialValues: {
    bookCategories: CategoryProps[];
  } = {
    bookCategories: book?.categories || [],
  };
  const formik = useFormik({
    initialValues,
    onSubmit: (values) => handleAddToDB(values),
  });

  const handleCategorySelect = (newValues: NewCategoryProps[]) => {
    formik.setFieldValue("categories", newValues);
  };

  //   const createBulkCategories = async ({ name }: { name: string }) => {
  //     const response = await createBulkCategoryEntries({
  //       categories: name,
  //     });
  //     setAllCategoryOptions((prev) => [...prev, response.data]);
  //   };

  return (
    <form onSubmit={formik.handleSubmit}>
      <MultiSelect
        options={allCategories}
        setAllCategoryOptions={setAllCategoryOptions}
        values={formik.values.bookCategories}
        name="bookCategories"
        onChange={handleCategorySelect}
      />
      <button type="submit">Submit</button>
    </form>
  );
};
