import { useState } from "react";

import { BookProps } from "../types";
import { Modal } from "../Modal/Modal";

import styles from "./Books.module.scss";

const {
  booksContainer,
  bookItem,
  bookInfoWrapper,
  bookTitle,
  bookDescription,
  imageContainer,
} = styles;

const getSanitizedDate = (date: Date) => {
  const year = date.getFullYear();
  const month = date.getMonth() + 1;
  const day = date.getDate();

  return [day, month, year].join("/");
};

const BookModal = () => {
  return (
    <Modal handleClose={() => setShowModal(false)} show={showModal}>
      <div>Hi</div>
    </Modal>
  );
};

export const Books = (props: { booksList: BookProps[] }) => {
  const [showModal, setShowModal] = useState(false);
  const { booksList } = props;
  return (
    <div className={booksContainer}>
      {showModal && <BookModal />}
      {booksList.length ? (
        booksList.map((b) => (
          <div className={bookItem} key={`book-${b.id}`}>
            <div className={bookInfoWrapper}>
              <div className={bookTitle}>{b.title}</div>
              <div>{b.authors}</div>
              <div>{getSanitizedDate(b.publishedDate)}</div>
              <div className={bookDescription}>{b.description}</div>
              <button onClick={() => setShowModal(true)}>show modal</button>
            </div>
            <div className={imageContainer}>
              <a href={b.previewLink} target="_blank">
                <img src={b.imageLink} />
              </a>
            </div>
          </div>
        ))
      ) : (
        <div>no books</div>
      )}
    </div>
  );
};
