import { Dispatch, SetStateAction, useState } from "react";

import { BookProps } from "../types";
import { Modal } from "../Modal/Modal";

import styles from "./Books.module.scss";

const {
  booksContainer,
  bookItem,
  bookModalWrapper,
  bookInfoWrapper,
  bookTitle,
  bookDescription,
  imageContainer,
} = styles;

const getSanitizedDate = (date: Date) => {
  const year = date.getFullYear();
  const month = date.getMonth() + 1;
  const day = date.getDate();

  return [day, month, year].join("/");
};

type BookModalProps = {
  handleShowModal: Dispatch<SetStateAction<string | null>>;
  book: BookProps | undefined;
};

const BookModal = ({ handleShowModal, book }: BookModalProps) => {
  return (
    <Modal handleClose={() => handleShowModal(null)}>
      <div className={bookModalWrapper}>
        <div className={bookInfoWrapper}>
          <div className={bookTitle}>{book?.title}</div>
          <div>{book?.authors}</div>
          <div className={bookDescription}>{book?.description}</div>
        </div>
        <div className={imageContainer}>
          <a href={book?.previewLink} target="_blank">
            <img src={book?.imageLink} />
          </a>
        </div>
      </div>
    </Modal>
  );
};

export const Books = (props: { booksList: BookProps[] }) => {
  const [showBookModal, setShowBookModal] = useState<string | null>(null);
  const { booksList } = props;
  return (
    <div className={booksContainer}>
      {showBookModal && (
        <BookModal
          handleShowModal={setShowBookModal}
          book={booksList.find((b) => b.id === showBookModal)}
        />
      )}
      {booksList.length ? (
        booksList.map((b) => (
          <div className={bookItem} key={`book-${b.id}`}>
            <div className={bookInfoWrapper}>
              <div className={bookTitle}>{b.title}</div>
              <div>{b.authors}</div>
              <div>{getSanitizedDate(b.publishedDate)}</div>
              <div className={bookDescription}>{b.description}</div>
              <button onClick={() => setShowBookModal(b.id)}>show modal</button>
            </div>
            <div className={imageContainer}>
              <a href={b.previewLink} target="_blank">
                <img src={b.imageLink} />
              </a>
            </div>
          </div>
        ))
      ) : (
        <div>no books</div>
      )}
    </div>
  );
};
